import React, { useEffect, useState } from 'react'
import "./Feed.css"
import CreateIcon from '@mui/icons-material/Create';
import InputOption from "./InputOption" 
import InsertPhotoIcon from '@mui/icons-material/InsertPhoto';
import EventNoteIcon from '@mui/icons-material/EventNote';
import ArticleIcon from '@mui/icons-material/Article';
import YouTubeIcon from '@mui/icons-material/YouTube';
import Post from "./Post";
import { db } from "./firebase";
import firebase from 'firebase/compat/app';
function Feed() {

    const [input, setInput] = useState('')
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        db.collection("posts").onSnapshot((snapshot) => 
            setPosts(
                snapshot.docs.map((doc) => ({
                 id: doc.id,
                 data: doc.data(),
                }))
            )
        );
    }, []);

    const sendPost = e => {
        e.preventDefault();

        db.collection('posts').add({
            name: 'Raghav Puri',
            description: 'this is a test',
            message: input,
            photoUrl: '',
        })
    }

    return (
        <div className="feed">
            <div className="feed_inputContainer">
                <div className="feed_input">
                    <CreateIcon />
                    <form>
                        <input value={input} onChange={e => setInput(e.target.value)}type="text" placeholder="Start a Post" />
                        <button onClick={sendPost} type='submit'>Send</button>
                    </form>
                </div>
                <div className="feed_InputOptions">
                    <InputOption Icon={InsertPhotoIcon} title="Photo" color="#70B5F9"/>
                    <InputOption Icon={YouTubeIcon} title="Video" color="#7FC15E"/>
                    <InputOption Icon={EventNoteIcon} title="Event" color="#E7A33E"/>
                    <InputOption Icon={ArticleIcon} title="Write article" color="#F5987E"/>
                </div>
            </div>
            {posts.map(({id, data: {name, description, message, photoUrl}}) => (
                <Post key={id}name={name} description={description} message={message} photoUrl={photoUrl}/>
            ))}
            <Post name="Raghav Puri" description="Woodinville high school student" message="Hi this is my first post!"/>
        </div>
    )
}

export default Feed
